import React from 'react';
import axios from 'axios';

class RightHeader extends React.Component{
        constructor(props){
            super(props);
            this.state ={
              popbox : false,           //set popup box state
              popbox : false,           //set popup box state
              category:"",
              myFile:"",
              userName:'',
              desc:'',
              currentUserId:'',
